let addLinePO = function(so_name,partner_name) {
    let inParams = []
    inParams.push([['origin', 'ilike', so_name],['partner_id.name','ilike',partner_name]])
    let params = []
    params.push(inParams)
    odoo.execute_kw('purchase.order', 'search', params, function (err, value) {
        if (err) {
            node.send({payload:err})
        } else {
            node.send({payload:{PO:value}})
            POline['order_id'] = value[0]
            let inParams = []
            inParams.push(value[0])
            let params = []
            params.push(inParams)
            params.push({'fields':['date_order']})
            odoo.execute_kw('purchase.order', 'read', params, function (err, value) {
                if (err) {
                    node.send({payload:err})
                } else {
                    node.send({payload:value})
                    POline['date_planned'] = value[0]['date_order']
                    node.send({payload:POline})
                    let inParams = []
                    inParams.push(POline)
                    let params = []
                    params.push(inParams)
                    odoo.execute_kw('purchase.order.line', 'create', params, function (err, value) {
                        if (err) {
                            node.send({payload:err})
                        } else {
                            node.send({payload:value})
                        }
                    })
                }
            })

        }
    })
}

let find_user_partner_id = function(uid,so_name) {
    let inParams = []
    inParams.push([uid])
    let params = []
    params.push(inParams)
    params.push({'fields':['partner_id']})
    odoo.execute_kw('res.users', 'read', params, function (err, value) {
        if (err) {
            node.send({payload:err})
        } else {
            node.send({payload:{partner_name:value[0]['partner_id'][1],soname:so_name}})
            node.send({payload:{partner_info:value}})
            addLinePO(so_name,value[0]['partner_id'][1])
        }
    })
}

