let odoo_xmlrpc = global.get('odoo_xmlrpc')
let xmlrpc = global.get('xmlrpc')
let jsonrpc = global.get('jayson')

let host = '172.17.0.5'
let port = 8069
let db = 'demo'
let user = 'gonzalez@example.com'
let pass = 'gonzalez'

let poll = function(uid,partner_id,partner_name) {
    let last = context.get('last') || 0

    let path = '/longpolling/poll'
    let client = jsonrpc.client.http('http://' + host + ':' + port + path)
    node.send({payload:{channel:db + '_' + partner_id.toString()}})
    client.request('call', {context: {uid:uid}, channels:[db + '_' + partner_id.toString()],last:last},{context: {lang: 'es_ES', uid:uid}}, function(err, error, result) {
        if(err){
            node.send({payload:err}) 
        } else {
            node.send({payload:result})
            if (result.length) {
                context.set('last',result[result.length - 1].id)
            }

        }
    })
}

let get_uid_partner = function (uid) {
    let path = '/jsonrpc'
    let client = jsonrpc.client.http('http://' + host + ':' + port + path)

    let inParams = []
    inParams.push([uid])
    let params = []
    params.push(inParams)
    params.push({'fields':['partner_id']})

    let fparams = [];
    fparams.push(db);
    fparams.push(uid);
    fparams.push(pass);
    fparams.push('res.users');
    fparams.push('read');
    for(let i = 0; i <params.length; i++){
        fparams.push(params[i]);
    }

    client.request('call', {service:'object', method:'execute_kw', args:fparams}, function(err, error, result) {
        if(err){
            node.send({payload:err}) 
        } else {
            node.send({payload:{partner:result[0].partner_id}})
            poll(uid,result[0].partner_id[0],result[0].partner_id[1])

        }
    })
}


let connect = function () {
    let path = '/jsonrpc'

    let client = jsonrpc.client.http('http://' + host + ':' + port + path)

    client.request('call', {service:'common', method:'login', args:[db, user, pass]}, function(err, error, result) {
        if(err){
            node.send({payload:err}) 
        } else {
            node.send({payload:{uid:result}})
            get_uid_partner(result)

        }
    })
}

connect()

return

